
func main() {
	assert(0, 0, "0")
	assert(42, 42, "42")
	assert(5, 5, "5")
	assert(41, 12+34-5, "12 + 34 - 5")
	assert(15, 5*(9-6), "5*(9-6)")
	assert(4, (3+5)/2, "(3+5)/2")
	assert(10, -10+20, "-10+20")
	assert(10, - -10, "- -10")
	assert(10, - -+10, "- - +10")

	assert(0, 0 == 1, "0==1")
	assert(1, 42 == 42, "42==42")
	assert(1, 0 != 1, "0!=1")
	assert(0, 42 != 42, "42!=42")

	assert(1, 0 < 1, "0<1")
	assert(0, 1 < 1, "1<1")
	assert(0, 2 < 1, "2<1")
	assert(1, 0 <= 1, "0<=1")
	assert(1, 1 <= 1, "1<=1")
	assert(0, 2 <= 1, "2<=1")

	assert(1, 1 > 0, "1>0")
	assert(0, 1 > 1, "1>1")
	assert(0, 1 > 2, "1>2")
	assert(1, 1 >= 0, "1>=0")
	assert(1, 1 >= 1, "1>=1")
	assert(0, 1 >= 2, "1>=2")

	assert(4294967297, 4294967297, "4294967297")
	assert(0, 1073741824*100/100, "1073741824 * 100 / 100")

	printf("\nOK\n")
}
